import { savePDF } from '@progress/kendo-react-pdf';

class DocService {
  createPdf = (html) => {
    savePDF(html, {
      paperSize: ['210mm', '300mm'],
      fileName: 'form.pdf',
      scale: '1',
    })
  }
}

const Doc = new DocService();
export default Doc;
