import { savePDF } from '@progress/kendo-react-pdf';

class DocService {
  createPdf = (html) => {
    savePDF(html, {
      paperSize: 'auto',
      fileName: 'form.pdf',
      scale: '2',
      forcePageBreak: '1',
    })
  }
}

const Doc = new DocService();
export default Doc;
