import { savePDF } from '@progress/kendo-react-pdf';

class DocService {
  createPdf = (html) => {
    savePDF(html, {
      paperSize: 'A4',
      fileName: 'form.pdf',
      margin: "2cm",
      padding: "2cm"
    })
  }
}

const Doc = new DocService();
export default Doc;
