import { savePDF } from '@progress/kendo-react-pdf';

class DocService {
  createPdf = (html) => {
    savePDF(html, {
      /* cara manual ngubah page */
      paperSize:["250mm", "297mm"], 
      fileName: 'form.pdf',
      scale: 0.5,
      margin: '1cm'
    })
  }
}

const Doc = new DocService();
export default Doc;
